mapper.insert:
INSERT INTO ROOM (ROOM_NO, BOARD_NO, RECRUITS_NUM, DIVISION, GENDER, TYPE, AREA, DEPOSIT, RENT, COST, CONTRAT)
VALUES ( default , #{boardNo}, #{recruitsNum}, #{division}
, #{gender}, #{type}, #{area}, #{deposit}
, #{rent}, #{cost}, #{contrat})

INSERT INTO ROOM_IMAGE ( ORIGIN_NAME, CHANGE_NAME, ROOM_NO, FILE_LEVEL) SELECT C.* FROM (
SELECT #{roomImgList.originName} as ORIGIN_NAME, #{roomImgList.changeName} as CHANGE_NAME, #{roomImgList.roomNo} as ROOM_NO, #{roomImgList.imgLevel} as FILE_LEVEL FROM DUAL
) C

mapper.select:
SELECT BOARD.BOARD_NO, USER_NO, TITLE, HOUSE_LATITUDE, HOUSE_LONGITUDE, THUMBNAIL, (SELECT MIN(RENT) FROM ROOM WHERE BOARD_NO = BOARD.BOARD_NO ) MIN_AMOUNT, (SELECT COUNT(*) FROM WISH_LIST WHERE BOARD_NO = BOARD.BOARD_NO AND USER_NO = #{userNo}) WISH FROM BOARD LEFT JOIN SHARE_HOUSE ON SHARE_HOUSE.BOARD_NO = BOARD.BOARD_NO WHERE CATEGORY_U_NO = '2' AND STATUS = 'Y'
AND HOUSE_LATITUDE BETWEEN #{C.swLat} AND #{C.neLat} AND HOUSE_LONGITUDE BETWEEN #{C.swLng} AND #{C.neLng}
AND BOARD.TITLE LIKE #{keyword}
ORDER BY CREATE_DATE
